  const { image, title, author, link, audioUrl, description, originalUrl  } = podcast;
  const [episodes, setEpisodes] = useState([]);

  useEffect(() => {
    fetch("https://feeds.podcastmirror.com/higherside-chats/rss")
      .then(response => response.json())
      .then(data => {
        // Set the podcasts state to the array of feeds from the API response
        setPodcasts(data.feeds);

      })
      .catch(err => console.error(err));
  }, []);
console.log(originalUrl);
