const MeteorShower = () => {
    // Use the useEffect hook to generate the meteor divs when the component is mounted
    useEffect(() => {
      const body = document.querySelector("body");

      // Set the number of meteor divs to be generated
      let meteorNumber = 25;
      for (let i = 1; i <= meteorNumber; i++) {
        body.innerHTML += `<div class="meteor-${i}"></div>`;
      }
    }, []); // The empty array as the second argument to useEffect will ensure that the effect is only run when the component is mounted

    // Return an empty div to render the meteor divs
    return <div></div>;
  };



  export default MeteorShower
