import React from 'react';
import AudioPlayer from './AudioPlayer';
// import Parser from 'rss-parser';

const PodcastContainer = ({ podcast }) => {
  return (
    <div className="podcast-container">
      {podcast.image ? (
        <img
          src={podcast.image}
          alt={podcast.title}
          onClick={() => window.open(podcast.link, '_blank')}
        />
      ) : (
        <img src="https://www.rbr.com/wp-content/uploads/unnamed-1-45.jpg" alt={podcast.title} />
      )}
      <h2>{podcast.title}</h2>
      <h3>{podcast.author}</h3>
      <AudioPlayer audioUrl={podcast.audioUrl} />
      <p>{podcast.description}</p>
    </div>
  );
};

export default PodcastContainer;
