import React, { useState, useEffect } from 'react';
import axios from 'axios';
import xml2js from 'xml2js';
imp

const PodcastContainer = ({ podcast }) => {
const { image, title, author, link, audioUrl, description, originalUrl } = podcast;
const [episodes, setEpisodes] = useState([]);

useEffect(() => {
// Make an HTTP GET request to the originalUrl
axios.get(originalUrl).then((response) => {
// Parse the XML data
xml2js.parseString(response.data, (err, result) => {
if (err) {
console.error(err);
} else {
// Extract the episode data
const episodesData = result.map((episode) => ({
title: episode.title[0],
audioUrl: episode.enclosure[0].$.url,
description: episode.description[0],
pubDate: episode.pubDate[0],
}));


      // Set the episodes data in the state
      setEpisodes(episodesData);
    }
  });
});
}, [originalUrl]);

return (
<div className="podcast-container">
{image ? (
<img
src={image}
alt={title}
onClick={() => window.open(link, '_blank')}
/>
) : (
        <img src="https://www.rbr.com/wp-content/uploads/unnamed-1-45.jpg" alt={title} />
      )}
      <h2>{title}</h2>
      <h3>{author}</h3>
      <AudioPlayer audioUrl={audioUrl} />
      <p>{description}</p>
    </div>
  );
};

export default PodcastContainer;
